table.trigger_table {width: 100%;}
table.trigger_table td {padding: 5px; background: #f6f6f6; border-top: 1px solid #fefefe; border-bottom: 1px solid #dadada; text-shadow: 1px 1px 1px #fff;}
.pageContents table.trigger_table td a {color: #394D55; font-weight: bold;}
table.trigger_table th {padding: 5px; background: #A4B5BE; color: #fff; border-top: 1px solid #C8D9E2; border-bottom: 1px solid #A1AFB6; font-weight: normal; text-shadow: 1px 1px 1px #8597A0; font-weight: bold;}
table.trigger_table tr {text-align: left;}
table.trigger_table tr.even td {background: #eceff0; border-top: 1px solid #eceff0;}